INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1010,'Ostentatious Display','General','Your extravagant jewelry and accessories give you an air of importance you can use to your advantage in many social situations.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1011,'TORCH HANDLING','General','In your grasp, a simple torch becomes a beacon.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1012,'Arcane Insight','General','Magical insight helps you evade attacks.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1013,'Bloody Vengeance','Combat','Your retaliatory strikes have long-lasting effects.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1014,'Measured Response','Combat','You believe that a conservative but consistent response guarantees success.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1015,'Riptide Attack','Combat','You can follow up a successful trip attempt with a drag maneuver.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1016,'Spiked Destroyer','Combat','You can attack with your armor spikes while using bull rush or overrun.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1017,'Steady Engagement','Combat','You can draw on your excellent balance to trip or disarm opponents when they try to disengage.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1018,'Firebrand','Combat','You can make use of a torch as a deadly, macelike weapon.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1019,'Ordered Mind','General','Your study of Hell''s laws improves your counterspells.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1020,'Destroy Identity','General','You are able to use your weapons to destroy some of your opponent''s most distinctive physical characteristics.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1021,'Fearsome Finish','General','When you slay enemies, you frighten other opponents.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1022,'Reject Poison','General','You are a careful poisoner, and know how to protect yourself against exposure to the ill effects of most substances.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1023,'Shadow Dodge','General','You use light and darkness to your advantage, confusing your enemies and confounding their senses.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1024,'Merciless Rush','General','You are a determined foe, and though hordes may stand against you, they will not stand long.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1025,'Squash Flat','General','You are able to bull rush an opponent straight to the ground.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1026,'Shatter Resolve','General','Damaging a foe with negative energy causes despair.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1027,'Bloodletting','General','When you attack with a piercing or slashing weapon, the wounds you cause bleed profusely.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1028,'Aldori Dueling Mastery','Combat','You have mastered the grueling fighting style perfected by the Aldori Swordlords.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1029,'Altitude Affinity','General','You have hardened your body against the grueling rigors of surviving at high altitudes.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1030,'Andoren Falconry','General','You know the secret Andoren arts of falconry developed by the Novotnian family of Darkmoon Vale.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1031,'Arcane Vendetta','General','The mistreatment of your people by an arcane society (such as Numeria''s Technic League or the White Witches of Irrisen) has made you particularly eager to inf lict harm on those who use arcane magic.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1032,'Careful Speaker','General','The paranoid terror that comes from living in a region with a dangerous government (such as Cheliax or Galt) has made you unusually aware of what you say.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1033,'Cypher Magic','General','Your intimacy with runes and the mysteries of ancient arcana has taught you secret ways to draw greater power from magical writings. These unusual methods were initially devised by the Cyphermages of Riddleport, but they have since spread far and wide through the Inner Sea region.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1034,'Cypher Script','General','Through researching lengthy, ancient, arcane scripts, you have discovered a more efficient method of recording spells. As with the Cypher Magic feat, Cypher Script was originally developed by the Cyphermages of Riddleport, but has been spreading through the Inner Sea region recently.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1035,'Demon Hunter','Combat','You are well-versed in demonic lore.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1036,'Dervish Dance','Combat','You have learned to turn your speed into power, even with a heavier blade.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1037,'Desert Dweller','General','Your time spent living in merciless deserts has granted you resistance to extreme heat, starvation, and thirst.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1038,'Desperate Battler','Combat','Your experiences fighting against numerous foes that have already overwhelmed your allies, friends, and family have forced you to excel at fighting on your own.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1039,'Eye of the Arclord','General','Your understanding of the esoteric teachings of the Arclords of Nex allows you to open an eldritch eye that grants you superior perception of the magical world.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1040,'Fey Foundling','General','You were found in the wilds as a child, bearing a mark of the First World.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1041,'Flagbearer','Combat','When brandishing a flag adorned with the standard of an organization you owe allegiance to, you inspire nearby members of the same allegiance.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1042,'Focused Discipline','Combat','Familiarity with the military traditions of your homeland inspires uncommon valor in you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1043,'Fortune Teller','General','As a result of being raised in a land steeped in tradition and superstition (such as Varisia or Ustalav), you are skilled at communicating with the spirit world.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1044,'Free Spirit','General','Your strong belief in the value of freedom protects you from mental and physical shackles.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1045,'Godless Healing','General','You have mastered a specialized and complex technique to ignore pain by focusing your belief on the self rather than relying on faith.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1046,'Green Faith Acolyte','General','You have trained to channel your magical energies in ways that do not harm the natural world around you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1047,'Hamatulatsu','Combat','You have mastered a deadly fighting form inspired by the hideous attacks of the barbed devil.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1048,'Harmonic Spell','General','You can weave bardic music effects into your spellcasting in such a way that your spellcasting and bardic performance become indistinguishable.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1049,'Harrowed','General','Numerous Harrow readings early in your life seem to have hit the mark precisely, increasing your belief that you are destined for a specific purpose in life.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1050,'Hermean Blood','General','You are the descendant of someone recruited to Hermea or the bastard result of a Hermean''s illicit pairing with someone foreign to that land.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1051,'Necromantic Affinity','General','Long exposure to necromantic energies has granted you a small measure of resistance against them.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1052,'Noble Scion','General','You are a member of a proud noble family, whether or not you remain in good standing with your family.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1053,'Rapid Reload','Combat','Choose a type of crossbow (hand, light, heavy) or a single type of one-handed or two-handed firearm that you are proficient with. You can reload such a weapon quickly.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1054,'Rugged Northerner','General','You live a hard life in a cold climate and gain some benefit from it.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1055,'Scholar','General','You have graduated from one of the many colleges, universities, and specialty schools of higher learning scattered throughout the Inner Sea region.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1056,'Secret Signs','General','You are particularly adept at communicating with others via innuendo, gestures, and secret hand signs.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1057,'Shade of the Uskwood','General','Ordained as one of the sinister albino druids who dwell within the heart of the Uskwood, you carry with you into the wider world a powerful effigy of hair, twigs, and blood that crawls with the deepening cold of the Midnight Lord''s hallowed hunting grounds.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1058,'Shrewd Tactician','Combat','Your dealings with pirates, thieves, and assassins have taught you to be exceedingly careful.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1059,'Stoic','General','You know from firsthand experience that life is a series of unforgiving trials, and that fear itself is something that can be conquered.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1060,'Storm-Lashed','General','A life spent enduring gales and storms has hardened your body to the elements.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1061,'Survivor','General','Only the strong thrive in your homeland, and you are no weakling, even among your kin.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1062,'Taldan Duelist','Combat','You trained at one of Taldor''s elite fighting schools and are skilled at rondelero, the art of fighting with the falcata (see the Advanced Player''s Guide) and buckler.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1063,'Totem Spirit','General','You are mystically tied to your tribe''s sacred totem.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1064,'Varisian Tattoo','General','You bear intricate tattoos that inspire and empower your natural magic ability. These tattoos mark you as a worker of the ancient traditions of Varisian magic. A Varisian tattoo typically consists of a long string of complex characters from the Thassilonian alphabet.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1065,'Wand Dancer','General','You are trained in a tradition of Garundi courtesans and court mages that melds dance with the use of magic wands.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1066,'Slaying Sprint','Combat','With swift feet and deadly intent, you finish off your target before anyone sees your blade.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1067,'Greater Serpent Lash','Combat','Your ability to disarm and pull foes off balance with your whip is impressive.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1068,'Serpent Lash','Combat','You are adept at vexing multiple foes with your whip.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1069,'Circling Offense','Combat','You are skilled at approaching large foes with an ally in a way that makes it difficult for your foes to find an advantage against you or your ally.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1070,'Footslasher','Combat','You are trained to strike at a large foe''s feet to impede its movement and cause great pain.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1071,'Topple Foe','Combat','You and your allies can work together to trip larger foes.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1072,'Jackal Heritage','General','A jackalwere crouches somewhere in your family tree.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1073,'Drunken Brawler','Combat','You have learned how to fight effectively while drunk.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1074,'Butterfly''s Sting','General','You can forgo a critical hit in order to pass it on to an ally.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1075,'Divination Guide','General','Consulting the agents of your deity, you gain more guidance than most would.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1076,'Bullseye Shot','Combat','You slow your breath, calm yourself, and hit the bullseye, just as you were trained to.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1077,'Nimble Natural Summons','General','Your summoned natural allies ignore difficult terrain in undergrowth.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1078,'Charge of the Righteous','Combat','You charge undead and fiends with confidence in your righteous cause.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1079,'Protector''s Strike','General','When you smite evil, your divine power protects a creature within line of sight.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1080,'Bestow Hope','General','Upon healing a creature with positive energy, you also give it hope.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1081,'Glorious Heat','General','When you cast divine fire spells, their heat empowers nearby allies.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1082,'Spear Dancer','General','You incorporate a two-handed reach weapon into a dizzying martial dance.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1083,'Stone Read','General','Even the faint movement and vibrations of stone can alert you to trouble.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1084,'Undermining Exploit','General','Your familiarity with earth and stone gives you a distinct advantage on rocky battlefields.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1085,'Ankle Biter','Combat','You know how take a mouthful out of anyone who tries to overpower you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1086,'Battle Singer','General','Your battle songs can drive your fellow goblins to new heights of frenzy.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1087,'Dog Killer, Horse Hunter','General','Embracing fear and hatred of your ancestral foes allows you to make particularly telling blows.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1088,'Lead from the Back','Combat','You allies depend far too much on your leadership and encouraging words for you to actually put yourself in harms'' way.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1089,'Letter Fury','General','You go wild when someone tries to steal the words from your head.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1090,'Roll With It','Combat','You know how to take a hit, even if your reaction sends you bouncing and flying out of battle while shrieking at the top of your lungs.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1091,'Saddle Shrieker','Combat','You sometimes get carried away when encouraging your mount to fight.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1092,'Combat Distraction','General','Anyone who attacks you while you act like a lunatic exposes themselves to brutal counterattacks from your allies.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1093,'Vandal','General','Even other goblins admire your talent for mindless destruction.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1094,'Ammo Drop','Combat','Your coordination is so perfect that you can simply drop ammunition from your hand at the exact moment required for it to fall into an open sling as your twirl it around.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1095,'Halfling Slinger','Combat','You have honed your racial talent for slingcraft.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1096,'Juggle Load','Combat','Your fingers are so nimble that reloading your sling is almost effortless for you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1097,'Large Target','Combat','The larger a creature is, the easier you find it to strike a vulnerable spot when attacking with a sling.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1098,'Whip-Slinger','Combat','You can use your sling as a sap to make attacks of opportunity.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1099,'Amplified Rage','General','When adjacent to other raging allies, your rages become even more powerful.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1100,'Blood Vengeance','General','Seeing an ally fall in combat fills you with a murderous fury.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1101,'Brutal Grappler','Combat','When helping an ally grapple, you can damage the target.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1102,'Destroyer''s Blessing','Combat','Breaking things adds to your power.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1103,'Ferocious Tenacity','Combat','You spit in the face of death.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1104,'Ferocious Tenacity','Combat','You spit in the face of death.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1105,'Fire God''s Blessing','General','The Fire God rewards you for killing enemies with flame.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1106,'Gore Fiend','Combat','Horrible wounds, whether on yourself or your enemies, makes your blood sing.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1107,'Sympathetic Rage','Combat','Seeing an ally enter a rage causes you to enter a rage-like state.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1108,'Thrill of the Kill','General','Killing fuels your rage.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1109,'Warleader''s Rage','General','Your rages are so inspiring to your allies that they don''t have to remain adjacent to you to stay raging.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1110,'Adept Channel','General','Some orc adepts enjoy a greater connection to their gods, and can channel their faith through their unholy symbols. You gain the ability to channel energy as a cleric.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1111,'Cold Celerity','General','You are a child of the frozen north, and you draw your strength from the biting cold and unforgiving conditions.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1112,'Tribal Scars','General','You endured the grueling coming-of-age rituals of your tribe or following, and proudly bear the scars that grant you the blessings of your tribe''s ancestors or totem.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1113,'Witchbreaker','Combat','Your training and skill in combating witches and hags have made you resistant to their magic, and you can disrupt their magical hatred.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1114,'DEADLY DEALER','General','Your skill with handling cards and arcane talents allow you to turn mundane cards into deadly weapons.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1115,'Thunder And Fang','General','You have mastered the ancient Shoanti Thunder and Fang fighting style, allowing you to fight with increased effectiveness when wielding an earth breaker and klar.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1116,'Chainbreaker','Achievement','Nothing rouses your fury and makes you more deadly in battle than the sight of slaves cowering before their masters.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1117,'Devil''s Foe','Achievement','Once you were just another helpless victim, but now even the deepest pits of Hell ring with the lamentations of your adversaries.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1118,'Eagle Knight Candidate','Achievement','You have proven your courage and skill to both the Eagle Knights and the leaders of Andoran.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1119,'Talmandor''s Lifting','Achievement','You felt the powerful wings of Talmandor surround you when you risked death from a fall, and somehow a tiny spark of his essence remains connected to your soul.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1120,'Advanced Defensive Combat Training','Combat','You are a master at defending yourself from all manner of combat maneuvers.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1121,'Bloodstone Manhunter','General','You are skilled at fighting criminal humans such as bandits.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1122,'Calm Disposition','General','You always appear to be in control of your situation.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1123,'Death''s Suitor','General','You have died and returned so often that even Pharasma now deems it part of your fate.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1124,'Disassemble Magic Item','General','You can take apart and reassemble magic items.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1125,'Divine Deception','General','You are skilled at manipulating divine magical items.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1126,'Fast Crawl','General','You are skilled at moving while prone.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1127,'Fearsome Barricade','Combat','Your wrath is as potent a barrier as your blades.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1128,'Grand Master Performer','General','You grant your allies even greater bonuses when using your bardic performance abilities.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1129,'Ki Diversity','Combat','You have trained at the feet of the masters of the Kusari- Gama, a secret society of monk warriors. With their secrets you have mastered new ways to apply your ki.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1130,'Let Them Come','Combat','You have trained to fight onrushing enemy hordes. With a carefully timed strike you ensure that their reckless charge will be their undoing.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1131,'Master Performer','General','When using your bardic performance abilities, you grant your allies greater bonuses.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1132,'Nameless Servitor','General','The fact that you have given up your real name makes you resistant to certain types of magic.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1133,'Old Cults Awakener','General','You hear the voices of the Old Ones, and believe it is your destiny to awaken others to their ancient cause using bizarre and torturous rituals. Whether your work leaves these individuals mad or truly touched by alien sentience remains unproven.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1134,'Ominous Mien','General','Your walk, stance, and voice all amplify your status as a Hellknight when you cast spells or use spell-like abilities against opponents.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1135,'Silent Performer','General','You can use any bardic performance ability without making a sound.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1136,'Touched By Sacred Fire','General','Regular contact with an elemental guardian of the Ruby Prince (such as Khemet''s invisible fire elemental cohort Janhelia) has left you able to shrug off many sources of unnatural heat, even if the effort required sometimes makes it difficult to defend yourself from attacks.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1137,'Verbose Performer','General','The range of your bardic performances increases.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1138,'Whispered Knowledge','General','You possess secret knowledge that enhances and helps perfect your undead form.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1139,'Blood Of Heroes','General','You have a sense of destiny about you and always seem to succeed, even when the odds are against you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1140,'Hero''s Fortune','General','Even at the start of your career, it was clear that you had a chance at greatness, and your legend continues to grow with every adventure.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1141,'Luck Of Heroes','General','To others, it seems that you always have a bit of luck around you.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1142,'Celestial Obedience','General','Your reverence for an empyreal lord is so great that daily prayer and minor sacrifices grant you special boons.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1143,'Accursed','Story','Your curse weighs down your soul like a millstone around your neck.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1144,'Arisen','Story','Escaping death strengthened your bond to life, but fills you with a need for answers.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1145,'Battlefield Healer','Story','In even the fiercest battles, you risk life and limb to save your allies.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1146,'Champion','Story','You must prove yourself through single combat.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1147,'Damned','Story','From your earliest days, you were destined to sacrifice everything in your quest for power.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1148,'Deny The Reaper','Story','The lives you could not save stay with you to your final breath.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1149,'Eldritch Researcher','Story','You seek new applications for magical energy.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1150,'Fearless Zeal','Story','You''re willing to lay down your life for your faith.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1151,'Feral Heart','Story','In your chest beasts the heart of a wild beast.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1152,'Foeslayer','Story','Your bitter feud with your enemies can be quenched only with blood.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1153,'Forgotten Past','Story','A pivotal event from your past eludes your memory.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1154,'Glimpse Beyond','Story','You have glimpsed the madness at the edges of reality.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1155,'Innocent Blood','Story','With their deaths, the pitiful wretches that inhabit this world open your path to greatness.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1156,'Liberator','Story','Your time in shackles has forever marked your soul.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1157,'Lost Legacy','Story','What once belonged to your family shall be yours again.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1158,'Magnum Opus','Story','You seek to create a true masterpiece.');
