INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1484,'Dirty Trick Master','Combat','You are adept at stifling your enemies and can expertly make a bad situation even worse.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1485,'Divert Harm','Combat','You know how to throw your enemies into harm''s way at the last second.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1486,'Crisis Of Conscience','Story','Your internal struggle and contradictory actions have left you teetering on a knife''s edge between good and evil. You must choose which path you will make your own.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1487,'Planar Hunter','General','After careful study of otherworldly environments, you can quickly evaluate how an outsider reacts in its home surroundings. You can use your knowledge of the outsider''s native environment to guide your attacks and overcome the outsider''s natural resistances.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1488,'Practiced Leadership','General','When you and your cohort are both members of the same organization, you needn''t shoulder the burden of leadership alone. By relying on the authority of your shared organization as well as your own leadership skills, your cohort demonstrates improved devotion and tactical acumen.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1489,'Fabulist','General','You enjoy telling tall tales and gain confidence when others believe them.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1490,'Gun Twirling','General','You can spin and juggle a small firearm, making it difficult to predict where you will shoot.');
INSERT INTO feats(id,feat_name,feat_type,description_text) VALUES (1491,'Named Bullet','General','You can craft ammunition intended to kill specific foes.');
